CREATE TABLE course(c_id MEDIUMINT NOT NULL AUTO_INCREMENT, crn VARCHAR(6) NOT NULL, subject VARCHAR(6) NOT NULL, course_number VARCHAR(4) NOT NULL, title VARCHAR(75) NOT NULL, section VARCHAR(4) NOT NULL, schedule_type VARCHAR(4) NOT NULL, term VARCHAR(6) NOT NULL, instructor VARCHAR(50) NOT NULL, a_plus_count int, a_count int, a_minus_count int, b_plus_count int, b_count int, b_minus_count int, c_plus_count int, c_count int, c_minus_count int, d_plus_count int, d_count int, d_minus_count int, f_count int, average_gpa decimal(3,2), CONSTRAINT course_desc UNIQUE(crn, subject, title, section, schedule_type, term), PRIMARY KEY(c_id));